int* merge_sort(int a[],int b[]){
    int a_size = sizeof(a)/sizeof(int);
    int b_size = sizeof(b)/sizeof(int);
    int tmp_size = a_size + b_size;
    int tmp[tmp_size];
    int s = 0;
    int a_s = 0;
    int b_s = 0;
    int a_e = a_size - 1;
    int b_e = b_size - 1;
    //
    //
    //
    while(a_s <= a_e && b_s <= b_e){
        if(a[a_s] > b[b_s]){
            tmp[s++] = a[a_s++];
        }
        else{
            tmp[s++] = b[b_s++];
        }
    }
    while(a_s <= a_e){
        tmp[s++] = a[a_s++];
    }
    while(b_s <= b_e){
        tmp[s++] = b[b_s++];
    }
    return tmp;
}
