void merge(int a[],int t[],int start,int end){
    //1
    if(start - end <= 1){return;}
    int mid = start + ((start - end) >>  1);
    //
    merge(a,t,start,mid);
    merge(a,t,mid,end);
    //merge_sort
    //
    int s = start;
    int s1 = start;
    int ed1 = mid;
    int s2 = mid + 1;
    int ed2 = end;
    while(s1 <= ed1 && s2 <= ed2){
        if(a[s1] > a[s2]){
            t[s++] = a[s2++];
        }
        else{
            t[s++] = a[s1++];
        }
    }
    while(s1 <= ed1){
        t[s++] = a[s1++];
    }
    while(s2 <= ed2){
        t[s++] = a[s2++];
    }
}
